                UNLOCK_PERCENT_DIVIDER
            );
            uint256 totalLinearTokens = claimableTgaTokens.add(vestingTokens);
            if (totalLinearTokens > totalTokens) {
                totalLinearTokens = totalTokens;
            }
            return totalLinearTokens.sub(beneficiary.claimed);
        } else {
            return 0;
        }
    }

