pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract ERC1155Mock is ERC1155 {
    mapping(uint256 => address) public creators;

    event Minted(
        address indexed creator,
        address indexed initialOwner,
        uint256 _tokenId,
        uint256 _quantity
    );

    string private baseUri = "";
    constructor(string memory _tokenUri) ERC1155(_tokenUri) {
        baseUri = _tokenUri;

        mint(_msgSender(), 1, 10000, "");
    }

    function mint(
        address _to,
        uint256 _id,
        uint256 _initialSupply,
        bytes memory data
    ) public {
        creators[_id] = _msgSender();
        _mint(_to, _id, _initialSupply, data);
        emit Minted(_msgSender(), _to, _id, _initialSupply);
    }

    function mintBatch(
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) public {
        _mintBatch(to, ids, amounts, data);
    }

    function burn(address account, uint256 id, uint256 amount) public {
        _burn(account, id, amount);
    }

    function burnBatch(
        address account,
        uint256[] memory ids,
        uint256[] memory amounts
    ) public {
        _burnBatch(account, ids, amounts);
    }

    function uri(uint256 tokenId) public view override returns (string memory) {
        require(_exists(tokenId), "ERC1155#uri: NONEXISTENT_TOKEN");
        return baseUri;
    }

    function _exists(uint256 _id) internal view returns (bool) {
        return creators[_id] != address(0);
    }
}
