contracts/mocks/OFTPlainToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract OFTPlainToken is ERC20 {
    constructor(
        string memory _name,
        string memory _symbol,
        address _owner
    ) ERC20(_name, _symbol) {
        _mint(_owner, 1000000000000000000000000);
    }

    function mint(address account, uint256 amount) public {
        require(
            amount <= 1000000000000000000000000,
            "Maximum mint amount exceeded - max 1,000,000 OFT"
        );
        _mint(account, amount);
    }
}

contracts/mocks/OFTV1.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@layerzerolabs/solidity-examples/contracts/token/oft/v2/OFTV2.sol";

contract OFTV1 is OFTV2 {
    constructor(
        string memory _name,
        string memory _symbol,
        address _lzEndpoint,
        address _owner
    ) OFTV2(_name, _symbol, 18, _lzEndpoint) {
        _transferOwnership(_owner);
        _mint(_owner, 1000000000000000000000000);
    }

    function mint(address account, uint256 amount) public {
        require(
            amount <= 1000000000000000000000000,
            "Maximum mint amount exceeded - max 1,000,000 OFT"
        );
        _mint(account, amount);
    }
}

